    # Remove/arrows button
    def remove_it(self):
        # Grab what's on the screen already
        screen = self.outputLabel.text()
        # Remove the last item in list/string
        screen[:-1]
        #Output back to the screen
        self.outputLabel.setText(screen)


    #Let's Do Some Math
    def math_it(self):
        # Grab what's on the screen already
        screen = self.outputLabel.text()
        
        try:
            # Do the math
            answer = eval(screen)
            # Output the answer to the screen
            self.outputLabel.setText(str(answer))
        except:
            self.outputLabel.setText("ERROR")


    #Change from positive to negative
    def plus_minus_it(self):
        # Grab what's on the screen already
        screen = self.outputLabel.text()
        if "-" in screen:
             self.outputLabel.setText(screen.replace("-",""))
        else:
            self.outputLabel.setText(f'-{screen}.')

    # Button "." function
    def dot_it(self):
        screen = self.outputLabel.text()
        if screen[-1] == ".":
            pass
        
        else:
            self.outputLabel.setText(f'{screen}.')

    # Press function
    def press_it(self,pressed):
        # Condition to use "C"
        if pressed == "C":
            self.outputLabel.setText("0")
        else:
            # Check to see if starts with 0 and delete 0
            if self.outputLabel.text() == "0":
                self.outputLabel.setText("")
            # Concatenate the pressed button with was there already
            self.outputLabel.setText(f'{self.outputLabel.text()}{pressed}')

